ENV['RACK_ENV'] = 'test'

require 'auto_scaling_server'

module AutoScaling
  class AutoScalingServerTest < Test::Unit::TestCase

    include Rack::Test::Methods

    def app
      AutoScalingServer
    end

    def setup
      config = YAML.load_file('config/config.yaml')
      @settings = config['endpoints'][ENV['RACK_ENV']]['opennebula']
    end

    def url
      username = @settings['username']
      password = @settings['password']
      uri = URI(@settings['endpoints']['appflow'])

      "#{uri.scheme}://#{username}:#{password}@#{uri.host}:#{uri.port}#{uri.path}"
    end

    def test_shall_deploy_a_service

      msg = {
          "name" => "myapp",
          "instances" => 1,
          "stack" => "java"
      }

      template_id = 120
      service_id = 144
      FakeWeb.register_uri(:post, "#{url}/service_template", :body => '{ "DOCUMENT": { "ID": "120" } }')
      FakeWeb.register_uri(:post, "#{url}/service_template/#{template_id}/action", :body => '{ "DOCUMENT": { "ID": "144" } }')

      post '/service', msg.to_json
      assert last_response.ok?

      # convergence
      master_id = 200
      FakeWeb.register_uri(:get, "#{url}/service/#{service_id}", :body => CONFIGURATION)
      FakeWeb.register_uri(:post, "http://192.168.122.100:4567/chef", :status => ["200", "OK"])
      post "/service/#{service_id}/container/#{master_id}"

      assert last_response.ok?
      service = Service.get(service_id)
      assert_equal :converged, service.status

      # monitoring data
      FakeWeb.register_uri(:post, "#{@settings['endpoints']['opennebula']}", {:status => ["200", "OK"], :body => MONITORING_DATA, :times => 10, :content_type => "text/xml"})

      #
      sleep 30

    end

